import React, { useState, useEffect } from 'react';
import '../styles/main.css';

const LandingPage = () => {
    const [activeSection, setActiveSection] = useState('home');
    const [stories, setStories] = useState([]);
    const [voteCredits, setVoteCredits] = useState(0);
    const [isPro, setIsPro] = useState(false);
    const [proMetrics, setProMetrics] = useState(null);

    useEffect(() => {
        loadAllStories();
    }, []);

    const showSection = (sectionId) => {
        setActiveSection(sectionId);
    };

    const loadAllStories = () => {
        const demoStories = [
            {
                id: 'story-1',
                author: 'Rahul Kumar',
                date: '2024-05-15',
                title: 'The Greatest Six I Ever Witnessed',
                excerpt: 'It was the final over, 12 runs needed, and Dhoni was on strike. The stadium was electric, 50,000 people on their feet...',
                votes: 247,
                boosted: true,
                priority: true
            },
            {
                id: 'story-2',
                author: 'Priya Sharma',
                date: '2024-05-12',
                title: 'My First Cricket Match',
                excerpt: 'I was seven years old when my father took me to my first cricket match. The roar of the crowd, the crack of the bat...',
                votes: 189,
                boosted: false,
                priority: false
            },
            {
                id: 'story-3',
                author: 'Amit Patel',
                date: '2024-05-10',
                title: 'The Catch That Changed Everything',
                excerpt: 'It was a routine match until that incredible boundary catch in the 18th over...',
                votes: 156,
                boosted: false,
                priority: false
            }
        ];
        setStories(demoStories);
    };

    const voteForStory = (storyId) => {
        if (voteCredits > 0) {
            setStories(prevStories =>
                prevStories.map(story =>
                    story.id === storyId
                        ? { ...story, votes: story.votes + 1 }
                        : story
                )
            );
            setVoteCredits(prev => prev - 1);
        } else {
            alert('Please purchase vote credits to vote for stories!');
        }
    };

    const boostStory = async (storyId) => {
        try {
            // Simulate Stripe payment
            const response = await fetch('/api/create-boost-session', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    storyId: storyId,
                    amount: 200 // $2.00 in cents
                })
            });

            if (response.ok) {
                setStories(prevStories =>
                    prevStories.map(story =>
                        story.id === storyId
                            ? { ...story, boosted: true }
                            : story
                    )
                );
            }
        } catch (error) {
            console.error('Failed to boost story:', error);
        }
    };

    const buyVotePack = async () => {
        try {
            // Simulate Stripe payment
            const response = await fetch('/api/create-vote-pack-session', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    amount: 300 // $3.00 in cents
                })
            });

            if (response.ok) {
                setVoteCredits(prev => prev + 5);
            }
        } catch (error) {
            console.error('Failed to purchase vote pack:', error);
        }
    };

    const subscribePro = async () => {
        try {
            // Simulate Stripe subscription
            const response = await fetch('/api/create-pro-subscription', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (response.ok) {
                setIsPro(true);
                loadProMetrics();
            }
        } catch (error) {
            console.error('Failed to subscribe to Pro:', error);
        }
    };

    const loadProMetrics = () => {
        setProMetrics({
            storiesSubmitted: 15,
            totalVotes: 234,
            proFeatures: ['Analytics', 'Priority Support', 'Custom Themes']
        });
    };

    const handleStorySubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const priority = formData.get('priority') === 'on';

        try {
            // Simulate API call
            const response = await fetch('/api/story-submit', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name: formData.get('author-name'),
                    email: formData.get('author-email'),
                    date: formData.get('match-date'),
                    story: formData.get('story-text'),
                    priority: priority
                })
            });

            if (response.ok) {
                alert('Story submitted successfully!');
                e.target.reset();
            }
        } catch (error) {
            console.error('Failed to submit story:', error);
        }
    };

    return (
        <div className="app">
            <header className="header">
                <nav className="nav container">
                    <a href="#" className="logo">CricketTales</a>
                    <ul className="nav-links">
                        <li><a href="#" onClick={() => showSection('home')} className={activeSection === 'home' ? 'active' : ''}>Home</a></li>
                        <li><a href="#" onClick={() => showSection('submit')} className={activeSection === 'submit' ? 'active' : ''}>Submit Story</a></li>
                        <li><a href="#" onClick={() => showSection('stories')} className={activeSection === 'stories' ? 'active' : ''}>Stories</a></li>
                        <li><a href="#" onClick={() => showSection('monetization')} className={activeSection === 'monetization' ? 'active' : ''}>Monetization</a></li>
                    </ul>
                </nav>
            </header>

            {activeSection === 'home' && (
                <section className="section active">
                    <div className="hero">
                        <div className="container">
                            <h1>Share Your Cricket Tales</h1>
                            <p>A community-driven platform where fans share match stories, player legends, and personal cricket memories</p>
                            <a href="#" onClick={() => showSection('submit')} className="btn btn-primary">Share Your Story</a>
                        </div>
                    </div>

                    <div className="container">
                        <div className="story-grid">
                            {stories.map(story => (
                                <div key={story.id} className="card story-card wicket-border">
                                    <div className="story-meta">
                                        <span>By {story.author}</span>
                                        <span>{story.date}</span>
                                    </div>
                                    <h3 className="story-title">{story.title}</h3>
                                    <p className="story-excerpt">{story.excerpt}</p>
                                    <div className="vote-widget">
                                        <div className="vote-count">
                                            <span>⭐</span>
                                            <span>{story.votes} votes</span>
                                        </div>
                                        <button className="btn-vote" onClick={() => voteForStory(story.id)}>Vote for This Tale</button>
                                    </div>
                                    <div className="boost-section">
                                        {story.boosted && <span className="boosted-badge">🚀 Boosted</span>}
                                        <button className="boost-btn" onClick={() => boostStory(story.id)}>Boost This Tale - $2</button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </section>
            )}

            {activeSection === 'submit' && (
                <section className="section active">
                    <div className="form-section">
                        <div className="container">
                            <div className="form-container">
                                <div className="card">
                                    <h2>Share Your Cricket Tale</h2>
                                    <form onSubmit={handleStorySubmit}>
                                        <div className="form-group">
                                            <label htmlFor="author-name">Your Name</label>
                                            <input type="text" id="author-name" name="author-name" className="form-control" required placeholder="Enter your name" />
                                        </div>

                                        <div className="form-group">
                                            <label htmlFor="author-email">Email Address</label>
                                            <input type="email" id="author-email" name="author-email" className="form-control" required placeholder="your.email@example.com" />
                                        </div>

                                        <div className="form-group">
                                            <label htmlFor="match-date">Match Date</label>
                                            <input type="date" id="match-date" name="match-date" className="form-control" required />
                                        </div>

                                        <div className="form-group">
                                            <label htmlFor="story-text">Your Cricket Story</label>
                                            <textarea id="story-text" name="story-text" className="form-control" required placeholder="Share your unforgettable cricket moment, match experience, or player encounter..."></textarea>
                                        </div>

                                        <div className="form-group">
                                            <label>
                                                <input type="checkbox" name="priority" /> Make Priority ($5)
                                            </label>
                                        </div>

                                        <button type="submit" className="btn btn-primary" style={{ width: '100%' }}>
                                            Submit Your Story
                                        </button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            )}

            {activeSection === 'stories' && (
                <section className="section active">
                    <div className="container">
                        <h2>All Cricket Tales</h2>
                        <div className="story-grid">
                            {stories.map(story => (
                                <div key={story.id} className="card story-card wicket-border">
                                    <div className="story-meta">
                                        <span>By {story.author}</span>
                                        <span>{story.date}</span>
                                    </div>
                                    <h3 className="story-title">{story.title}</h3>
                                    <p className="story-excerpt">{story.excerpt}</p>
                                    <div className="vote-widget">
                                        <div className="vote-count">
                                            <span>⭐</span>
                                            <span>{story.votes} votes</span>
                                        </div>
                                        <button className="btn-vote" onClick={() => voteForStory(story.id)}>Vote for This Tale</button>
                                    </div>
                                    <div className="boost-section">
                                        {story.boosted && <span className="boosted-badge">🚀 Boosted</span>}
                                        <button className="boost-btn" onClick={() => boostStory(story.id)}>Boost This Tale - $2</button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </section>
            )}

            {activeSection === 'monetization' && (
                <section className="section active">
                    <div className="container">
                        <h2>Monetization Features</h2>
                        <div className="monetization-grid">
                            <div className="monetization-card">
                                <h2>⭐ Vote Credits</h2>
                                <p>You have {voteCredits} vote credits remaining.</p>
                                <button className="btn btn-primary" onClick={buyVotePack}>Buy 5 Votes ($3)</button>
                            </div>

                            <div className="monetization-card">
                                <h2>🏏 CricketPro</h2>
                                <p>{isPro ? 'You are a Pro member!' : 'Upgrade to Pro for exclusive features'}</p>
                                {!isPro && (
                                    <button className="btn btn-primary" onClick={subscribePro}>Subscribe to Pro ($10/month)</button>
                                )}
                            </div>

                            {isPro && proMetrics && (
                                <div className="pro-dashboard">
                                    <h2>Pro Dashboard</h2>
                                    <div className="pro-metrics">
                                        <div className="metric-card">
                                            <div className="metric-value">{proMetrics.storiesSubmitted}</div>
                                            <div className="metric-label">Stories Submitted</div>
                                        </div>
                                        <div className="metric-card">
                                            <div className="metric-value">{proMetrics.totalVotes}</div>
                                            <div className="metric-label">Total Votes</div>
                                        </div>
                                        <div className="metric-card">
                                            <div className="metric-value">{proMetrics.proFeatures.length}</div>
                                            <div className="metric-label">Pro Features</div>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </section>
            )}

            <footer className="footer">
                <div className="container">
                    <p>&copy; 2025 CricketTales.com - Powered by Community Stories</p>
                </div>
            </footer>
        </div>
    );
};

export default LandingPage;
