import React from 'react';
import { Link, Outlet } from 'react-router-dom';
import '../styles/main.css';

const Layout = () => {
    return (
        <div className="app">
            <header className="header">
                <nav className="nav container">
                    <Link to="/" className="logo">CricketTales</Link>
                    <ul className="nav-links">
                        <li><Link to="/" className={({ isActive }) => isActive ? 'active' : ''}>Home</Link></li>
                        <li><Link to="/submit" className={({ isActive }) => isActive ? 'active' : ''}>Submit Story</Link></li>
                        <li><Link to="/stories" className={({ isActive }) => isActive ? 'active' : ''}>Stories</Link></li>
                        <li><Link to="/monetization" className={({ isActive }) => isActive ? 'active' : ''}>Monetization</Link></li>
                    </ul>
                </nav>
            </header>

            <main>
                <Outlet />
            </main>

            <footer className="footer">
                <div className="container">
                    <p>&copy; 2025 CricketTales.com - Powered by Community Stories</p>
                </div>
            </footer>
        </div>
    );
};

export default Layout;
